import { Table, Menu, Dropdown, Button } from "antd";
import { DownOutlined } from "@ant-design/icons";
import "./App.css";
import "antd/dist/antd.min.css";

function App() {
  return (
    <div className="App">
      <NestedTable />
    </div>
  );
}

const handleAdd = () => {};

const menu = (
  <Menu>
    <Menu.Item>Action 1</Menu.Item>
    <Menu.Item>Action 2</Menu.Item>
  </Menu>
);

const data = [
  {
    key: 1,
    id: 1,
    name: "This is production name",
    description: "Upgraded: 56",
    status: "IN PROGRESS",
    parentId: 0,
  },
  {
    key: 2,
    id: 2,
    name: "This is production name",
    description: "Upgraded: 56",
    status: "IN PROGRESS",
    parentId: 0,
  },
  {
    key: 3,
    id: 3,
    name: "This is production name",
    description: "Upgraded: 56",
    status: "IN PROGRESS",
    parentId: 2,
  },
  {
    key: 4,
    id: 4,
    name: "This is production name",
    description: "Upgraded: 56",
    status: "IN PROGRESS",
    parentId: 3,
  },
];

const rowSelection = {
  onChange: (selectedRowKeys, selectedRows) => {
    console.log(
      `selectedRowKeys: ${selectedRowKeys}`,
      "selectedRows: ",
      selectedRows
    );
  },
  getCheckboxProps: (record) => ({
    disabled: record.name === "Disabled User",
    // Column configuration not to be checked
    name: record.name,
  }),
};

function NestedTable() {
  const expandedRowRender = () => {
    const columns = [
      { title: "Task ID", dataIndex: "id", key: "id" },
      { title: "Name", dataIndex: "name", key: "name" },
      { title: "Description", dataIndex: "description", key: "description" },
      { title: "Status", dataIndex: "status", key: "status" },
      {
        title: "Action",
        dataIndex: "operation",
        key: "operation",
        render: () => (
          <span className="table-operation">
            <Dropdown overlay={menu}>
              <span>
                More <DownOutlined />
              </span>
            </Dropdown>
          </span>
        ),
      },
    ];
    return (
      <Table
        rowSelection={{
          type: "checkbox",
          ...rowSelection,
        }}
        columns={columns}
        dataSource={data}
        pagination={false}
      />
    );
  };

  const columns = [
    { title: "Task ID", dataIndex: "id", key: "id" },
    { title: "Name", dataIndex: "name", key: "name" },
    { title: "Description", dataIndex: "description", key: "description" },
    {
      title: "Status",
      dataIndex: "status",
      key: "status",
      filters: [
        {
          text: "IN PROGRESS",
          value: "IN PROGRESS",
        },
        {
          text: "DONE",
          value: "DONE",
        },
        {
          text: "COMPLETE",
          value: "COMPLETE",
        },
      ],
      onFilter: (value, record) => record.status.indexOf(value) === 0,
    },
    {
      title: "Action",
      dataIndex: "operation",
      key: "operation",
      render: () => (
        <span className="table-operation">
          <Dropdown overlay={menu}>
            <span>
              More <DownOutlined />
            </span>
          </Dropdown>
        </span>
      ),
    },
  ];

  return (
    <div>
      <Button
        onClick={handleAdd}
        type="primary"
        style={{
          margin: 16,
        }}
      >
        Add a task
      </Button>
      <Table
        className="components-table-demo-nested"
        rowSelection={{
          type: "checkbox",
          ...rowSelection,
        }}
        columns={columns}
        expandable={{ expandedRowRender }}
        dataSource={data}
      />
    </div>
  );
}

export default App;
