import { Table, Menu, Dropdown, Button, Form, Input, Modal, Radio } from "antd";
import { DownOutlined } from "@ant-design/icons";
import "./App.css";
import "antd/dist/antd.min.css";

function App() {
  const [visible, setVisible] = useState(false);

  const onCreate = (values) => {
    console.log("Received values of form: ", values);
    setVisible(false);
  };
  return (
    <div className="App">
      <Button
        type="primary"
        onClick={() => {
          setVisible(true);
        }}
      >
        New Collection
      </Button>
      <CollectionCreateForm
        visible={visible}
        onCreate={onCreate}
        onCancel={() => {
          setVisible(false);
        }}
      />
      <NestedTable style={{}} />
    </div>
  );
}

const handleAdd = () => {};

const menu = (
  <Menu>
    <Menu.Item>Action 1</Menu.Item>
    <Menu.Item>Action 2</Menu.Item>
  </Menu>
);

const data = [
  {
    key: 1,
    id: 1,
    name: "Start the task",
    description: "Start Working On This Task Thingy",
    status: "IN PROGRESS",
    parentId: 0,
  },
  {
    key: 2,
    id: 2,
    name: "Implement Circular Dependency Check",
    description:
      "While adding a new task and selecting parentId, check for dependency loop",
    status: "IN PROGRESS",
    parentId: 0,
  },
  {
    key: 3,
    id: 3,
    name: "Add A New Task",
    description: "Develop a popup form to add a new task to the list",
    status: "IN PROGRESS",
    parentId: 2,
  },
  {
    key: 4,
    id: 4,
    name: "Edit Task Functionality",
    description: "Edit a task in-line and then save or cancel",
    status: "IN PROGRESS",
    parentId: 3,
  },
];

const rowSelection = {
  onChange: (selectedRowKeys, selectedRows) => {
    console.log(
      `selectedRowKeys: ${selectedRowKeys}`,
      "selectedRows: ",
      selectedRows
    );
  },
  getCheckboxProps: (record) => ({
    disabled: record.name === "Disabled User",
    // Column configuration not to be checked
    name: record.name,
  }),
};

function NestedTable() {
  const expandedRowRender = () => {
    const columns = [
      { title: "Task ID", dataIndex: "id", key: "id" },
      { title: "Name", dataIndex: "name", key: "name" },
      { title: "Description", dataIndex: "description", key: "description" },
      { title: "Status", dataIndex: "status", key: "status" },
      {
        title: "Action",
        dataIndex: "operation",
        key: "operation",
        render: () => (
          <span className="table-operation">
            <Dropdown overlay={menu}>
              <span>
                More <DownOutlined />
              </span>
            </Dropdown>
          </span>
        ),
      },
    ];
    return (
      <Table
        rowSelection={{
          type: "checkbox",
          ...rowSelection,
        }}
        columns={columns}
        dataSource={data}
        pagination={false}
      />
    );
  };

  const columns = [
    { title: "Task ID", dataIndex: "id", key: "id" },
    { title: "Name", dataIndex: "name", key: "name" },
    { title: "Description", dataIndex: "description", key: "description" },
    {
      title: "Status",
      dataIndex: "status",
      key: "status",
      filters: [
        {
          text: "IN PROGRESS",
          value: "IN PROGRESS",
        },
        {
          text: "DONE",
          value: "DONE",
        },
        {
          text: "COMPLETE",
          value: "COMPLETE",
        },
      ],
      onFilter: (value, record) => record.status.indexOf(value) === 0,
    },
    {
      title: "Action",
      dataIndex: "operation",
      key: "operation",
      render: () => (
        <span className="table-operation">
          <Dropdown overlay={menu}>
            <span>
              More <DownOutlined />
            </span>
          </Dropdown>
        </span>
      ),
    },
  ];

  return (
    <div>
      <Table
        className="components-table-demo-nested"
        rowSelection={{
          type: "checkbox",
          ...rowSelection,
        }}
        columns={columns}
        expandable={{ expandedRowRender }}
        dataSource={data}
      />
    </div>
  );
}

const CollectionCreateForm = ({ visible, onCreate, onCancel }) => {
  const [form] = Form.useForm();
  return (
    <Modal
      visible={visible}
      title="Create a new collection"
      okText="Create"
      cancelText="Cancel"
      onCancel={onCancel}
      onOk={() => {
        form
          .validateFields()
          .then((values) => {
            form.resetFields();
            onCreate(values);
          })
          .catch((info) => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        form={form}
        layout="vertical"
        name="form_in_modal"
        initialValues={{
          modifier: "public",
        }}
      >
        <Form.Item
          name="title"
          label="Title"
          rules={[
            {
              required: true,
              message: "Please input the title of collection!",
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item name="description" label="Description">
          <Input type="textarea" />
        </Form.Item>
        <Form.Item
          name="modifier"
          className="collection-create-form_last-form-item"
        >
          <Radio.Group>
            <Radio value="public">Public</Radio>
            <Radio value="private">Private</Radio>
          </Radio.Group>
        </Form.Item>
      </Form>
    </Modal>
  );
};
export default App;
