import { useEffect, useState } from "react";
import { Button } from "antd";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";
import "./App.css";
import "antd/dist/antd.min.css";
import { typeOfStatus } from "./constant/status";
import List from "./components/List/List";
import ListForm from "./components/ListForm/ListForm";
import {
  addChildToTask,
  getCurrentStatus,
  updateParent,
  updateTaskById,
} from "./utils/helpers";

const api = axios.create({
  baseURL: 'http://localhost:5000'
})

function App() {
  const [visible, setVisible] = useState(false);
  const [lastId, setLastId] = useState(0);
  const [data, setData] = useState([]);
  const [selectedRowKeys, setSelectedRowKeys] = useState([]);
  const [flatTree, setFlatTree] = useState([]);
  let flat = new Set();
  let checks = new Set();

  useEffect(() => {
    if (data.length > 0) {
      const keys = data.map((element) => getCheckedTask(element))[0];
      const tree = data.map((element) => flatten(element))[0];
      setFlatTree(Array.from(tree));
      setSelectedRowKeys(Array.from(keys));
    }
    // eslint-disable-next-line
  }, [data]);

  useEffect(() => {
    const url = "/gettasks";
    api.get(url).then((tasks) => {
      const fetchedTasks = tasks.data.map((task) => {
        if(JSON.parse(task.children) !== null){
          parseChildren(task.children);
        }

        task.key = uuidv4();
        return task;
      });
      setData([...fetchedTasks]);
      if (flatTree.length > 0) {
        setLastId(Math.max(...flatTree.map((value) => value.task_id)));
      }
    });
  }, [flatTree]);

  function flatten(element) {
    flat.add({
      name: element.name,
      task_id: element.task_id,
      parentId: element.parentId,
    });

    if (element.children !== null) {
      for (let i = 0; i < element.children.length; i++) {
        flatten(element.children[i]);
      }
    }
    return flat;
  }

  function getCheckedTask(element) {
    if (
      element.status === typeOfStatus.DONE ||
      element.status === typeOfStatus.COMPLETE
    ) {
      checks.add(element.key);
    }
    if (element.children !== null) {
      for (let i = 0; i < element.children.length; i++) {
        getCheckedTask(element.children[i]);
      }
    }
    return checks;
  }

  function parseChildren(element) {
    if (element.children !== null) {
      element.children = JSON.parse(element.children);
      for (let i = 0; i < element.children.length; i++) {
        parseChildren(element.children[i]);
      }
    }
    return element;
  }

  let countDependency = 0;
  let countCompletedDependency = 0;
  function searchTaskList(element, matchingId) {
    if (
      element.parentId === matchingId &&
      element.status === typeOfStatus.COMPLETE
    ) {
      countCompletedDependency++;
      countDependency++;
    } else if (element.parentId === matchingId) {
      countDependency++;
    } else if (element.children !== null) {
      let result = null;
      for (let i = 0; result === null && i < element.children.length; i++) {
        result = searchTaskList(element.children[i], matchingId);
      }
    }
    return null;
  }

  const updateTaskList = (record) => {
    const dataCopy = [...data];
    const updatedCopy = dataCopy.map((element) => {
      countDependency = 0;
      countCompletedDependency = 0;
      searchTaskList(element, record.task_id);
      const currentStatus = getCurrentStatus(element, record.task_id);
      switch (currentStatus) {
        case typeOfStatus.IN_PROGRESS:
          if (
            countDependency === 0 ||
            countCompletedDependency === countDependency
          ) {
            updateTaskById(
              record.task_id,
              element,
              "status",
              typeOfStatus.COMPLETE
            );
            updateParent(record.parentId, element, typeOfStatus.COMPLETE);
          } else {
            updateTaskById(
              record.task_id,
              element,
              "status",
              typeOfStatus.DONE
            );
          }
          break;
        case typeOfStatus.COMPLETE:
        case typeOfStatus.DONE:
          if (record.children === null) {
            updateTaskById(
              record.task_id,
              element,
              "status",
              typeOfStatus.IN_PROGRESS
            );
          }
          updateParent(record.parentId, element, typeOfStatus.DONE);
          break;
        default:
          break;
      }
      return element;
    });
    setData([...updatedCopy]);
  };

  const onCreate = async ({ name, parent }) => {
    const newTask = {
      key: uuidv4(),
      task_id: lastId + 1,
      name,
      status: typeOfStatus.IN_PROGRESS,
      parentId: parent || 0,
      children: null,
    };
    setLastId(lastId + 1);
    if (!parent) {
       try{
        await api.post('/addtask', newTask);
        setData([...data, newTask]);
       }catch(e){
        console.log(e);
       }  
    } else {
      const dataCopy = [...data];
      const updatedCopy = dataCopy.map((element) =>
        addChildToTask(parent, element, newTask)
      );
      setData([...updatedCopy]);
    }

    setVisible(false);
  };
  return (
    <div className="App">
      <Button
        type="primary"
        onClick={() => {
          setVisible(true);
        }}
      >
        Add New Task
      </Button>
      <ListForm
        visible={visible}
        onCreate={onCreate}
        flatTree={flatTree}
        onCancel={() => {
          setVisible(false);
        }}
      />
      <List
        updateTaskList={updateTaskList}
        data={data}
        selectedRowKeys={selectedRowKeys}
        setSelectedRowKeys={setSelectedRowKeys}
      />
    </div>
  );
}
export default App;
