import { Table, Dropdown } from "antd";
import { typeOfStatus } from "../../constant/status";

export default function List({ updateTaskList, data }) {
  const expandedRowRender = () => {
    const nested_columns = [
      { title: "Task ID", dataIndex: "id", key: "id" },
      { title: "Name", dataIndex: "name", key: "name" },
      { title: "Description", dataIndex: "description", key: "description" },
      { title: "Status", dataIndex: "status", key: "status" },
      {
        title: "Action",
        dataIndex: "operation",
        key: "operation",
        render: () => (
          <span className="table-operation">
            <Dropdown>
              <span>Edit | Delete</span>
            </Dropdown>
          </span>
        ),
      },
    ];
    return (
      <Table
        rowSelection={{
          type: "checkbox",
          hideSelectAll: true,
          onSelect: (record) => {
            updateTaskList(record.id);
          },
        }}
        columns={nested_columns}
        dataSource={[...data]}
        pagination={false}
      />
    );
  };

  const columns = [
    { title: "Task ID", dataIndex: "id", key: "id" },
    { title: "Name", dataIndex: "name", key: "name" },
    { title: "Description", dataIndex: "description", key: "description" },
    {
      title: "Status",
      dataIndex: "status",
      key: "status",
      filters: [
        {
          text: typeOfStatus.IN_PROGRESS,
          value: typeOfStatus.IN_PROGRESS,
        },
        {
          text: typeOfStatus.DONE,
          value: typeOfStatus.DONE,
        },
        {
          text: "Complete",
          value: "Complete",
        },
      ],
      onFilter: (value, record) => record.status.indexOf(value) === 0,
    },
    {
      title: "Action",
      dataIndex: "operation",
      key: "operation",
      render: () => (
        <span className="table-operation">
          <Dropdown>
            <span>EDIT | DELETE</span>
          </Dropdown>
        </span>
      ),
    },
  ];
  return (
    <div>
      <Table
        className="components-table-demo-nested"
        rowSelection={{
          type: "checkbox",
          hideSelectAll: true,
          onSelect: (record) => {
            updateTaskList(record.id);
          },
        }}
        columns={columns}
        expandable={{ expandedRowRender }}
        dataSource={[...data]}
        pagination={{ pageSize: 20, position: ["bottomCenter"] }}
      />
    </div>
  );
}
