import { v4 as uuidv4 } from "uuid";
import { typeOfStatus } from "../constant/status";

export function updateTaskById(id, data, property, value) {
  if (data.task_id === id) {
    data[property] = value;
    data["key"] = uuidv4();
  }
  if (data.children !== null && data.children.length > 0) {
    for (let i = 0; i < data.children.length; i++) {
      data.children[i] = updateTaskById(id, data.children[i], property, value);
    }
  }
  return data;
}

export function addChildToTask(parentId, data, task) {
  if (data.task_id === parentId) {
    if (data.children === null) {
      data.children = [task];
      data["key"] = uuidv4();
    } else {
      data.children.push(task);
    }
    //UPDATE QUERY
  }
  if (data.children !== null && data.children.length > 0) {
    for (let i = 0; i < data.children.length; i++) {
      data.children[i] = addChildToTask(parentId, data.children[i], task);
    }
  }
  return data;
}

export function updateGrandparent(taskId, incomingData) {
    incomingData.map((element) => {
      const grandParentsStatus = getCurrentStatus(element, taskId);
      if (grandParentsStatus === typeOfStatus.DONE) {
        updateTaskById(taskId, element, "status", typeOfStatus.COMPLETE);
      }
      const GP = getParentId(element, taskId);
      if(GP) updateGrandparent(GP, incomingData)
    });
}

export function getParentId(element, matchingId){
  if (element.task_id === matchingId) {
    return element.parentId;
  } else if (element.children !== null) {
    let result = null;
    for (let i = 0; result === null && i < element.children.length; i++) {
      result = getParentId(element.children[i], matchingId);
    }
    return result;
  }
  return null;
}

export function getCurrentStatus(element, matchingId) {
  if (element.task_id === matchingId) {
    return element.status;
  } else if (element.children !== null) {
    let result = null;
    for (let i = 0; result === null && i < element.children.length; i++) {
      result = getCurrentStatus(element.children[i], matchingId);
    }
    return result;
  }
  return null;
}
