import { useState } from "react";
import { Button } from "antd";
import { v4 as uuidv4 } from "uuid";
import "./App.css";
import "antd/dist/antd.min.css";
import { typeOfStatus } from "./constant/status";
import List from "./components/List/List";
import ListForm from "./components/ListForm/ListForm";

function App() {
  const [visible, setVisible] = useState(false);
  const [data, setData] = useState([
    {
      key: 1,
      id: 1,
      name: "Start the task",
      description: "Start Working On This Task Thingy",
      status: typeOfStatus.DONE,
      parentId: 0,
    },
    {
      key: 2,
      id: 2,
      name: "Implement Circular Dependency Check",
      description:
        "While adding a new task and selecting parentId, check for dependency loop",
      status: typeOfStatus.COMPLETE,
      parentId: 0,
    },
    {
      key: 3,
      id: 3,
      name: "Add A New Task",
      description: "Develop a popup form to add a new task to the list",
      status: typeOfStatus.IN_PROGRESS,
      parentId: 2,
    },
    {
      key: 4,
      id: 4,
      name: "Edit Task Functionality",
      description: "Edit a task in-line and then save or cancel",
      status: typeOfStatus.IN_PROGRESS,
      parentId: 3,
    },
  ]);
  const [selectedRowKeys, setSelectedRowKeys] = useState(
    data
      .filter((_data) => _data.status === typeOfStatus.DONE)
      .map((item) => item.key)
  );

  const updateTaskList = (id, selectedRows) => {
    const keys = selectedRows.map((row) => row.key);
    setSelectedRowKeys(keys);
    const selectedTaskIndex = data.findIndex((_data) => _data.id === id);
    const dataCopy = [...data];
    if (dataCopy[selectedTaskIndex].status === typeOfStatus.IN_PROGRESS) {
      dataCopy[selectedTaskIndex].status = typeOfStatus.DONE;
      // dataCopy[selectedTaskIndex].key = uuidv4();
      setData(dataCopy);
    } else {
      dataCopy[selectedTaskIndex].status = typeOfStatus.IN_PROGRESS;
      // dataCopy[selectedTaskIndex].key = uuidv4();
      setData(dataCopy);
    }
  };

  const onCreate = ({ name, parentId }) => {
    const newTask = {
      key: uuidv4(),
      id: data.length + 1,
      name,
      description: name,
      status: typeOfStatus.IN_PROGRESS,
      parentId: parentId || 0,
    };
    const dataCopy = [...data];
    dataCopy.push(newTask);
    setData(dataCopy);
    setVisible(false);
  };
  return (
    <div className="App">
      <Button
        type="primary"
        onClick={() => {
          setVisible(true);
        }}
      >
        Add New Task
      </Button>
      <ListForm
        visible={visible}
        onCreate={onCreate}
        onCancel={() => {
          setVisible(false);
        }}
      />
      <List
        updateTaskList={updateTaskList}
        data={data}
        selectedRowKeys={selectedRowKeys}
        setSelectedRowKeys={setSelectedRowKeys}
      />
    </div>
  );
}
export default App;
