import { Table } from "antd";
import { ListAttributes} from "../../constant/listAttributes";
import { typeOfStatus } from "../../constant/status";
import "./List.css";

export default function List({
  updateTaskList,
  data,
  selectedRowKeys,
  setSelectedRowKeys,
}) {
  const columns = [
    { title: "Task ID", dataIndex: "id", key: "id" },
    { title: "Name", dataIndex: "name", key: "name" },
    {
      title: "Status",
      dataIndex: "status",
      key: "status",
      render: (status) => <span style={{color:'red'}}>&#9673;<span>{status}</span></span>,
      filters: [
        {
          text: typeOfStatus.IN_PROGRESS,
          value: typeOfStatus.IN_PROGRESS,
        },
        {
          text: typeOfStatus.DONE,
          value: typeOfStatus.DONE,
        },
        {
          text: typeOfStatus.COMPLETE,
          value: typeOfStatus.COMPLETE,
        },
      ],
      onFilter: (value, record) => record.status.indexOf(value) === 0,
    },
  ];
  const {subListIndent, pageSize, paginationPosition, rowSelectionType}  = ListAttributes;
  return (
    <div>
      <Table
        className="components-table-demo-nested"
        rowSelection={{
          selectedRowKeys,
          type: rowSelectionType,
          hideSelectAll: true,
          onSelect: (record, _, selectedRows) => {
            updateTaskList(record);
            setSelectedRowKeys(selectedRows.map((row) => row.key));
          },
        }}
        columns={columns}
        indentSize={subListIndent}
        dataSource={[...data]}
        pagination={{
          pageSize: pageSize,
          position: paginationPosition,
        }}
      />
    </div>
  );
}
