import { useEffect, useState } from "react";
import { Button } from "antd";
import { v4 as uuidv4 } from "uuid";
import "./App.css";
import "antd/dist/antd.min.css";
import { typeOfStatus } from "./constant/status";
import List from "./components/List/List";
import ListForm from "./components/ListForm/ListForm";

function App() {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    const [data, setData] = useState([
      {
        key: uuidv4(),
        id: 1,
        name: "Start the task",
        description: "Start Working On This Task Thingy",
        status: typeOfStatus.IN_PROGRESS,
        parentId: 0,
        total_dependencies: 0,
        dependencies_done: 0,
        dependencies_complete: 0,
      },
      {
        key: uuidv4(),
        id: 2,
        name: "Implement Circular Dependency Check",
        description:
          "While adding a new task and selecting parentId, check for dependency loop",
        status: typeOfStatus.IN_PROGRESS,
        parentId: 0,
        total_dependencies: 0,
        dependencies_done: 0,
        dependencies_complete: 0,
      },
      {
        key: uuidv4(),
        id: 3,
        name: "Add A New Task",
        description: "Develop a popup form to add a new task to the list",
        status: typeOfStatus.DONE,
        parentId: 2,
        total_dependencies: 0,
        dependencies_done: 0,
        dependencies_complete: 0,
      },
      {
        key: uuidv4(),
        id: 4,
        name: "Edit Task Functionality",
        description: "Edit a task in-line and then save or cancel",
        status: typeOfStatus.IN_PROGRESS,
        parentId: 3,
        total_dependencies: 0,
        dependencies_done: 0,
        dependencies_complete: 0,
      },
    ]);
    const updatedData = data?.forEach((element) => {
      element.total_dependencies = data?.filter(
        (task) => task.parentId === element.id
      ).length;
      element.dependencies_done = data?.filter(
        (task) =>
          task.parentId === element.id || task.status === typeOfStatus.DONE
      ).length;
      element.dependencies_complete = data?.filter(
        (task) =>
          task.parentId === element.id || task.status === typeOfStatus.COMPLETE
      ).length;
    });
    console.log("updated", updatedData);
    // setData(updatedData);
  }, [data]);

  const updateTaskList = (record) => {
    const selectedTaskIndex = data.findIndex((_data) => _data.id === record.id);
    const dataCopy = [...data];
    if (dataCopy[selectedTaskIndex].status === typeOfStatus.IN_PROGRESS) {
      dataCopy[selectedTaskIndex].status = typeOfStatus.DONE;
      dataCopy[selectedTaskIndex].key = record.key;
      setData(dataCopy);
      console.log(data);
    } else {
      dataCopy[selectedTaskIndex].status = typeOfStatus.IN_PROGRESS;
      dataCopy[selectedTaskIndex].key = record.key;
      setData(dataCopy);
    }
  };

  const onCreate = (values) => {
    console.log("Received values of form: ", values);
    setVisible(false);
  };
  return (
    <div className="App">
      <Button
        type="primary"
        onClick={() => {
          setVisible(true);
        }}
      >
        Add New Task
      </Button>
      <ListForm
        visible={visible}
        onCreate={onCreate}
        onCancel={() => {
          setVisible(false);
        }}
      />
      <List updateTaskList={updateTaskList} data={data} />
    </div>
  );
}
export default App;
